import React, { useRef } from 'react';
import { View, ImageBackground } from 'react-native';
import PagerView from 'react-native-pager-view';
import { useNavigation } from '@react-navigation/native';

import Page from './components/Page';
import Footer from './components/Footer';


const InstructionPage = () => {
  const navigation = useNavigation();

  const pagerRef = useRef(null);

  const handlePageChange = pageNumber => {
    pagerRef.current.setPage(pageNumber);
  };

  return (
    <View style={{ flex: 1, backgroundColor: '#fff' }}>
      <PagerView style={{ flex: 1 }} 
        initialPage={0} 
        ref={pagerRef}
        // showPageIndicator
        // pageIndicatorProps={{ activeDotColor: '#222222', inactiveDotColor: 'gray' }} // currently not working but dev team are working on it
        >
        <View key="1">
          <ImageBackground source={require('../../assets/vector_bg.svg')} resizeMode='cover' style={styles.bac}>
          <Page
            title="How KUShare works?"
            detail="    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Massa eros fames ullamcorper odio integer. Eu venenatis aliquam sit urna tristique accumsan porta."
            imageSource={require('../../assets/Exams_1.gif')}
          />
          <Footer
            rightButtonLabel="Next"
            rightButtonPress={() => {
              handlePageChange(1);
            }}
          />
          </ImageBackground>
        </View>
        <View key="2">
          <Page
            title="Welcome to the weather app"
            detail="    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Massa eros fames ullamcorper odio integer. Eu venenatis aliquam sit urna tristique accumsan porta."

          />
          <Footer
            rightButtonLabel="Next"
            rightButtonPress={() => {
              handlePageChange(2);
            }}
          />
        </View>
        <View key="3">
          <Page
            title="Get updates on weather"
            detail="    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Massa eros fames ullamcorper odio integer. Eu venenatis aliquam sit urna tristique accumsan porta."

          />
          <Footer
            rightButtonLabel="Get Started"
            rightButtonPress={() => {
              navigation.navigate('Home');
            }}
          />
        </View>
      </PagerView>
    </View>
  );
};

export default InstructionPage;
