import React from 'react';
import { View, Text, Image, ImageBackground } from 'react-native';


const Page = ({ title, detail, imageSource }) => {
  return (
    <ImageBackground source={require('../../../assets/favicon.png')} resizeMode="cover" style={{ flex: 1, justifyContent: 'center', width: null, height: null }}>
    <View
      style={{
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#fff'
      }}
    >
      <Image source={imageSource} style={{width: 375, height: 375, overlayColor: '#fff' }} />
      <View style={{ marginTop: 16, paddingHorizontal: 30 }}>
        <Text style={{ fontFamily: 'Source Sans Pro', fontSize: 28, fontWeight: '900', textAlign: 'center', color: '#04DB8B' }}>
          {title}
        </Text>
        <Text style={{ fontFamily: 'Prompt', fontSize: 18, fontWeight: 'normal', color: '#000000', marginTop: 20 }}>
            {detail}
        </Text>
      </View>
    </View>
    </ImageBackground>
  );
};

export default Page;
